// ✅ Final working version of InvoiceCartReview.js with correct invoiceNo syntax and updated PDF view

import React, { useState, useEffect } from 'react';
import exportInvoiceExcel from "../utils/exportInvoiceExcel";
import PDFInvoice from "./PDFInvoice";

export default function InvoiceCartReview({
  cart,
  vehicleName,
  vehicleNumber,
  notes,
  onSubmit,
  setTab,
  buyersList
}) {
  const [selectedBuyer, setSelectedBuyer] = useState(null);
  const [invoiceData, setInvoiceData] = useState([]);
  const [customInvoiceNo, setCustomInvoiceNo] = useState("");
  const [buyerSearch, setBuyerSearch] = useState("");
  const [showPDF, setShowPDF] = useState(false);

  const invoiceNo = `2025-26/${customInvoiceNo}`;

  useEffect(() => {
    const allEntries = cart.flatMap(item =>
      item.entries.filter(e => e.batch && e.qty > 0 && e.ctn > 0).map(entry => ({
        name: item.name,
        batch: entry.batch,
        mfg: entry.mfg,
        exp: entry.exp,
        qty: entry.qty * entry.ctn,
        rate: entry.rate || 0,
        billedQty: entry.qty * entry.ctn,
        discount: 0,
      }))
    );
    setInvoiceData(allEntries);
  }, [cart]);

  useEffect(() => {
    if (selectedBuyer) {
      const updated = invoiceData.map(item => {
        let billedQty = item.qty;
        if (selectedBuyer.scheme === "3+1") {
          billedQty = Math.round(item.qty * 3 / 4);
        } else if (selectedBuyer.scheme === "5+1") {
          billedQty = Math.round(item.qty * 5 / 6);
        }
        return {
          ...item,
          billedQty,
          discount: selectedBuyer.dis1 || 0,
        };
      });
      setInvoiceData(updated);
    }
  }, [selectedBuyer]);

  const handleChange = (index, field, value) => {
    const newData = [...invoiceData];
    newData[index][field] = value;
    setInvoiceData(newData);
  };

  const grandTotal = invoiceData.reduce((sum, item) => {
    const billed = parseFloat(item.billedQty) || 0;
    const rate = parseFloat(item.rate) || 0;
    const discount = parseFloat(item.discount) || 0;
    const gross = billed * rate;
    return sum + (gross - (gross * discount / 100));
  }, 0);

  const handlePrintAll = () => {
    if (!selectedBuyer) return alert("कृपया Buyer चुनें।");

    exportInvoiceExcel(invoiceData, selectedBuyer, grandTotal);

    const msg = [
      `🧾 *Invoice from ${selectedBuyer.name}*`,
      `No: ${invoiceNo}`,
      ...invoiceData.map((item, i) => `${i + 1}. ${item.name} | Qty: ${item.qty} | Billed: ${item.billedQty} | ₹${item.rate}`),
      `\nTotal: ₹${grandTotal.toFixed(2)}`
    ].join("\n");

    window.open(`https://wa.me/919929988408?text=${encodeURIComponent(msg)}`, '_blank');

    setShowPDF(true);
    setTimeout(() => {
      window.print();
      onSubmit();
    }, 500);
  };

  const handleAddMore = () => {
    setTab("categories");
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const filteredBuyers = buyersList.filter(b => b.name.toLowerCase().includes(buyerSearch.toLowerCase()));

  return (
    <>
      {/* Buyer Input and Table UI (same as before) */}
      <div className="print:hidden text-center mt-4">
        <button disabled={!selectedBuyer} onClick={handlePrintAll} className={`px-5 py-2 rounded-full text-white font-semibold ${selectedBuyer ? 'bg-green-600 hover:bg-green-700' : 'bg-gray-300 cursor-not-allowed'}`}>
          🖨️ Print & Send
        </button>
        <button onClick={handleAddMore} className="px-5 py-2 rounded-full bg-purple-600 hover:bg-purple-700 text-white font-semibold">
          ➕ Add More
        </button>
      </div>

      {showPDF && selectedBuyer && (
        <div className="hidden print:block">
          <PDFInvoice cart={invoiceData} buyer={{ ...selectedBuyer, invoiceNumber: invoiceNo }} grandTotal={grandTotal} />
        </div>
      )}
    </>
  );
}
