import { useState, useEffect } from "react";
import products from "../data/products"; // fallback if needed

export default function useProductFilter() {
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Churna");
  const [productList, setProductList] = useState([]);

  useEffect(() => {
    // Fetch from Google Sheets or fallback
    fetch("https://script.google.com/macros/s/your-script-id/exec")
      .then((res) => res.json())
      .then((data) => {
        setProductList(data || []);
      })
      .catch((err) => {
        console.warn("Using fallback products.js");
        setProductList(products); // fallback if fetch fails
      });
  }, []);

  const filteredProducts = productList?.filter((p) => {
    if (search.trim()) return p.name?.toLowerCase().includes(search.toLowerCase());
    return p.category === selectedCategory;
  });

  return {
    products: productList,
    filteredProducts,
    search,
    setSearch,
    selectedCategory,
    setSelectedCategory
  };
}
