// Updated ReviewPanel.js
import React from "react";
import exportTallyExcel from '../utils/exportTallyExcel';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

export default function ReviewPanel({
  cart,
  vehicleName,
  vehicleNumber,
  notes,
  dealer,
  onPrint,
  onSendWhatsapp
}) {
  const totalCTN = cart.reduce((sum, item) =>
    sum + item.entries.reduce((eSum, e) => eSum + (e.ctn || 0), 0), 0);

  const handleExportTallyExcel = () => {
    exportTallyExcel(cart);
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();
    const date = new Date().toLocaleDateString('en-GB');

    doc.setFontSize(16);
    doc.text("🪔 JAI Ayurvedic Billing Dept", 14, 20);
    doc.setFontSize(10);
    doc.text(`Invoice Date: ${date}`, 14, 28);
    doc.text(`Dealer: ${dealer?.["Dealer Name"] || "Unknown"}`, 14, 34);
    doc.text(`Vehicle: ${vehicleName} (${vehicleNumber})`, 14, 40);
    if (notes) doc.text(`Notes: ${notes}`, 14, 46);

    const rows = cart.flatMap((item, idx) =>
      item.entries.map((entry, bIdx) => [
        idx + 1,
        item.name,
        entry.batch,
        entry.mfg,
        entry.exp,
        entry.qty,
        entry.ctn,
        (entry.qty || 0) * (entry.ctn || 0)
      ])
    );

    doc.autoTable({
      startY: 55,
      head: [["S.No", "Product Name", "Batch", "MFG", "EXP", "Qty/CTN", "CTN", "Total Qty"]],
      body: rows,
      styles: { fontSize: 8 }
    });

    const finalY = doc.lastAutoTable.finalY || 75;

    doc.text(`Total Cartons: ${totalCTN}`, 14, finalY + 10);
    doc.text(`Signature: ______________________`, 14, finalY + 20);

    doc.save(`Invoice_${Date.now()}.pdf`);
  };

  return (
    <div className="max-w-3xl mx-auto bg-white rounded-xl shadow-md p-6 space-y-4 print:text-sm">
      <h2 className="text-xl font-bold text-center border-b pb-2">📋 Order Review</h2>

      {cart.map((item, i) => (
        <div key={i} className="border p-3 rounded-md bg-gray-50">
          <h3 className="font-semibold text-gray-800">{item.name}</h3>
          <table className="w-full mt-2 text-sm">
            <thead>
              <tr className="text-left text-gray-500">
                <th>Batch</th>
                <th>Qty</th>
                <th>CTN</th>
              </tr>
            </thead>
            <tbody>
              {item.entries.map((entry, idx) => (
                <tr key={idx} className="border-t">
                  <td className="py-1">{entry.batch}</td>
                  <td className="py-1">{entry.qty}</td>
                  <td className="py-1">{entry.ctn}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}

      <div className="mt-6 space-y-1">
        <p><strong>🚛 Vehicle:</strong> {vehicleName}</p>
        <p><strong>🔢 Vehicle Number:</strong> {vehicleNumber}</p>
        <p><strong>📦 Total CTNs:</strong> {totalCTN}</p>
        {notes && <p><strong>📝 Notes:</strong> {notes}</p>}
      </div>

      <div className="flex justify-center gap-4 mt-6 print:hidden">
        {dealer?.Type === "Billing" ? (
          <div className="flex flex-col gap-3">
            <button
              onClick={handleExportTallyExcel}
              className="bg-blue-500 hover:bg-blue-600 text-white font-semibold px-6 py-3 rounded-lg"
            >📄 Export Excel (Tally)</button>

            <button
              onClick={handleGeneratePDF}
              className="bg-purple-500 hover:bg-purple-600 text-white font-semibold px-6 py-3 rounded-lg"
            >🖨 Generate Invoice PDF</button>
          </div>
        ) : (
          <button
            onClick={onSendWhatsapp}
            className="bg-green-600 hover:bg-green-700 text-white font-semibold px-6 py-3 rounded-lg"
          >📲 Send to WhatsApp</button>
        )}
      </div>
    </div>
  );
}
