const buildWhatsappMessage = (dealer, cart, qty) => {
  if (!dealer || cart.length === 0) return '';

  const date = new Date();
  const formattedDate = date.toLocaleString('en-IN', {
    day: '2-digit',
    month: 'short',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
  });

  const lines = [
    `ü™î *Order from ${dealer["Dealer Name"] || "Unknown Dealer"}*`,
    `üìû ${dealer["Phone Number"] || "N/A"}`,
    `üïí ${formattedDate}`,
    ``,
    `üì¶ Items:`,
    ...cart.map((item, i) => `${i + 1}. ${item.name} ‚Äî ${qty[item.name] || 0} pcs`),
    ``,
    `üôè ‡§ß‡§®‡•ç‡§Ø‡§µ‡§æ‡§¶! ‡§ï‡•É‡§™‡§Ø‡§æ WhatsApp ‡§Æ‡•á‡§Ç *Send* ‡§¨‡§ü‡§® ‡§¶‡§¨‡§æ‡§è‡§Ç`,
  ];

  const message = lines.join("\n");
  const phone = '919829280873'; // Admin's WhatsApp number

  const whatsappURL = `https://wa.me/${phone}?text=${encodeURIComponent(message)}`;
  return whatsappURL;
};

export default buildWhatsappMessage;
