// utils/submitOrder.js
import exportToCSV from './exportToCSV';
import buildWhatsappMessage from './buildWhatsappMessage';
import sendViaWhatsApp from './sendViaWhatsApp';

export default function submitOrder({ cart, dealer, vehicleName, vehicleNumber, notes, totalCTN }) {
  const dealerName = dealer?.["Dealer Name"] || "Unknown Dealer";

  // Download CSV
  exportToCSV(cart, dealerName, vehicleName, vehicleNumber, notes);

  // Build WhatsApp message
  const message = buildWhatsappMessage(cart, vehicleName, vehicleNumber, notes, dealerName, totalCTN);

  // Send to WhatsApp admin
  sendViaWhatsApp("919929988408", message);
}
