import React from "react";

export default function ProductGrid({ items, cart, onAdd }) {
  const grouped = {};

  items.forEach((item) => {
    const parts = (item["Item Name"] || "").split(" ");
    const key = parts[0];
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(item);
  });

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
      {Object.entries(grouped).map(([group, groupItems]) => (
        <div key={group} className="bg-white border rounded-lg shadow-md p-4">
          <h2 className="text-lg font-bold text-blue-800 mb-2">{group}</h2>
          <ul className="space-y-2">
            {groupItems.map((item, i) => (
              <li key={i} className="flex justify-between items-center">
                <span className="font-medium text-gray-700">
                  {item.name}

                </span>
                <button
                  onClick={() => onAdd(item)}
                  className="text-sm text-green-600 font-semibold hover:underline"
                >
                  + Add
                </button>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}
