import React from "react";
import submitOrder from "../utils/submitOrder";
import sendViaWhatsApp from "../utils/sendViaWhatsApp";

export default function ReviewPanel({
  cart,
  dealer,
  vehicleName,
  vehicleNumber,
  notes,
  totalCTN,
  onPrint,
  onAddMore,
  showToast
}) {
  const handleFinalSubmit = () => {
    const result = submitOrder({
      cart,
      dealer,
      vehicleName,
      vehicleNumber,
      notes,
      totalCTN,
      showToast
    });

    if (result.success) {
      const confirmSend = window.confirm("‚úÖ Excel downloaded.\nSend WhatsApp now?");
      if (confirmSend) {
        sendViaWhatsApp(result.phoneNumber, result.msg); // ‚úÖ Safe for WhatsApp
      }
    }
  };

  const totalItems = cart.length;

  return (
    <div className="max-w-3xl mx-auto bg-white rounded-xl shadow-md p-6 space-y-4 print:text-sm">
      <h2 className="text-xl font-bold text-center border-b pb-2 print:hidden">üìã Order Review</h2>

      <div>
        {cart.map((item, i) => (
          <div key={i} className="mb-4">
            <p className="font-semibold">{i + 1}. {item.name}</p>
            {item.entries.map((e, j) => (
              <div key={j} className="ml-4 text-sm text-gray-700">
                Batch: {e.batch || '-'}, Qty: {e.qty || 0}, CTN: {e.ctn || 0}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="space-y-2">
        <p><strong>üöõ Vehicle:</strong> {vehicleName || 'N/A'} ({vehicleNumber || 'N/A'})</p>
        <p><strong>üì¶ Total CTNs:</strong> {totalCTN || 0}</p>
        {notes && <p><strong>üìù Notes:</strong> {notes}</p>}
      </div>

      <div className="flex gap-4 pt-4 print:hidden">
        <button
          className="w-full bg-gray-300 text-black py-3 rounded-xl font-bold text-lg"
          onClick={onAddMore}
        >
          ‚ûï Add More Items
        </button>

        <button
          className="w-full bg-green-600 text-white py-3 rounded-xl font-bold text-lg"
          onClick={handleFinalSubmit}
        >
          üì§ Send Order to WhatsApp
        </button>
      </div>
    </div>
  );
}
