import React from 'react';

export default function ProductGrid({ items = [], cart = [], onAdd = () => {} }) {
  if (items.length === 0) {
    return <p className="text-center text-gray-500 mt-6">No products found.</p>;
  }

  return (
    <div className="px-2 sm:px-4 space-y-6">
      {items.map((product, index) => {
        const firstBatch = product.batches?.[0] || {};
        return (
          <div
            key={index}
            className="min-w-[250px] bg-white border border-blue-100 rounded-2xl p-5 shadow-md hover:shadow-lg transition-all"
          >
            {/* Product Name */}
            <h4 className="text-md font-bold text-gray-800 mb-1">{product.name || "Unnamed Product"}</h4>

            {/* MRP Display */}
            <p className="text-sm text-gray-500 mb-1">
              üí∞ <span className="font-semibold">MRP:</span> ‚Çπ{product.MRP || "N/A"}
            </p>

            {/* Batch Info (if available) */}
            {firstBatch.batch && (
              <p className="text-sm text-gray-500 mb-1">
                üì¶ Batch: {firstBatch.batch} | üè≠ MFG: {firstBatch.mfg} | ‚è≥ EXP: {firstBatch.exp}
              </p>
            )}

            {/* Category */}
            <p className="text-xs text-gray-400 italic mb-2">{product.category || "Uncategorized"}</p>

            {/* Add to Cart Button */}
            <button
              onClick={() => onAdd(product)}
              disabled={cart.some((c) => c.name === product.name)}
              className={`w-full py-2 text-sm rounded-xl font-bold transition-all duration-200 shadow-md ${
                cart.some((c) => c.name === product.name)
                  ? 'bg-blue-300 text-white cursor-not-allowed'
                  : 'bg-blue-500 text-white hover:bg-blue-600'
              }`}
            >
              {cart.some((c) => c.name === product.name) ? '‚úîÔ∏è Added' : '‚ûï Add to Order'}
            </button>
          </div>
        );
      })}
    </div>
  );
}
