import { motion } from "framer-motion";
import React, { useEffect, useState, useRef } from 'react';

export default function CartReview({ cart, qty, onQtyChange, onRemove, onSubmit, setTab, batchData, setBatchData }) {
  const [shaking, setShaking] = useState({});
  const inputRefs = useRef({});

  const handleBatchChange = (itemName, field, value) => {
    setBatchData((prev) => ({
      ...prev,
      [itemName]: {
        ...prev[itemName],
        [field]: value
      }
    }));
  };

  useEffect(() => {
    const initialShake = {};
    let firstMissing = null;

    cart.forEach(item => {
      if (!qty[item.name]) {
        initialShake[item.name] = true;
        if (!firstMissing) firstMissing = item.name;
      }
    });

    setShaking(initialShake);

    if (firstMissing && inputRefs.current[firstMissing]) {
      inputRefs.current[firstMissing].scrollIntoView({ behavior: "smooth", block: "center" });
    }

    const timer = setTimeout(() => setShaking({}), 500);
    return () => clearTimeout(timer);
  }, [cart, qty]);

  if (cart.length === 0)
    return <p className="text-center text-gray-600">🛒 No items added.</p>;

  return (
    <div className="max-w-3xl mx-auto space-y-3 px-2 pb-24">
      <h3 className="text-center text-lg font-semibold text-gray-700 border-b pb-2 animate-glow">
        📝 कृपा यहां पर अपनी मात्रा दर्ज करें, फिर सबमिट ऑर्डर बटन दबाएं
      </h3>

      {cart.map((item, i) => (
        <div key={i} className="bg-white border border-blue-200 rounded-xl p-3 shadow-sm space-y-2">
          <div className="flex justify-between items-center">
            <h3 className="text-sm sm:text-base font-bold text-gray-800">{item.name}</h3>
            <button
              onClick={() => onRemove(item.name)}
              className="text-red-500 hover:text-red-700 text-sm"
            >
              🗑️ Remove
            </button>
          </div>

          <div className="flex flex-wrap items-center gap-2">
            {[12, 24, 36, 48, 72, 96].map((n, index) => (
              <motion.button
                key={n}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05, duration: 0.3 }}
                onClick={() => onQtyChange(item.name, n)}
                className="bg-blue-100 text-blue-700 text-sm px-2.5 py-1 rounded-full hover:bg-blue-200 transition font-semibold"
              >
                {n}
              </motion.button>
            ))}

            <div className="flex flex-col items-start gap-1 mt-1">
              <input
                ref={(el) => (inputRefs.current[item.name] = el)}
                type="number"
                value={qty[item.name] || ""}
                onChange={(e) => onQtyChange(item.name, parseInt(e.target.value) || "")}
                min="1"
                className={`w-24 px-3 py-2 border-2 rounded text-right font-bold text-lg shadow-sm
                  ${!qty[item.name] ? 'border-red-400 bg-red-50' : 'border-green-400 bg-green-50'}
                  ${shaking[item.name] ? 'shake' : ''}`}
              />
              <span className="text-sm text-gray-500 ml-1">pcs</span>
              {!qty[item.name] && (
                <p className="text-red-500 text-sm mt-1 ml-1">⚠️ Please enter qty</p>
              )}
            </div>
          </div>

          {/* Batch, MFG, EXP */}
          <div className="flex flex-wrap gap-2 mt-2 text-sm">
            <input
              type="text"
              placeholder="Batch No."
              value={batchData[item.name]?.batch || ""}
              onChange={(e) => handleBatchChange(item.name, 'batch', e.target.value)}
              className="px-2 py-1 border rounded w-[90px]"
            />
            <input
              type="text"
              placeholder="MFG"
              value={batchData[item.name]?.mfg || ""}
              onChange={(e) => handleBatchChange(item.name, 'mfg', e.target.value)}
              className="px-2 py-1 border rounded w-[75px]"
            />
            <input
              type="text"
              placeholder="EXP"
              value={batchData[item.name]?.exp || ""}
              onChange={(e) => handleBatchChange(item.name, 'exp', e.target.value)}
              className="px-2 py-1 border rounded w-[75px]"
            />
          </div>
        </div>
      ))}

      {/* Sticky Bottom Buttons + Plant */}
      <div className="fixed bottom-0 left-0 w-full bg-white shadow-inner border-t border-gray-200 py-3 px-4 flex justify-center items-center gap-4 z-50">
        <button
          onClick={() => setTab('categories')}
          className="bg-blue-100 text-blue-700 font-semibold text-sm px-4 py-2 rounded-lg hover:bg-blue-200 transition"
        >
          🔙 Add More Items
        </button>

        <img
          src="/plant.svg"
          alt="Plant"
          className="w-10 h-10 animate-sway"
        />

        <button
          onClick={onSubmit}
          className="bg-green-600 hover:bg-green-700 text-white font-bold text-sm px-5 py-2 rounded-lg shadow-md transition"
        >
          ✅ SUBMIT ORDER
        </button>
      </div>
    </div>
  );
}
