import { useState, useEffect } from "react";
import { setProductData } from "../utils/getBatchDetails";

export default function useProductFilter() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("à¤ªà¤¾à¤•");


useEffect(() => {
  setProductData(finalProducts); // ðŸ‘ˆ Link live data to batch lookup
}, [finalProducts]);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch(
          "https://script.google.com/macros/s/AKfycbyuBBpR3LjZftoWLmuJlABy4vex0CRSCweCq3MS8vh9klL7uTy1C3UO2Yfvx0eX6JE/exec?key=DPRTMNT54$"
        );
        const data = await res.json();

        const grouped = {};

        data.forEach((row) => {
          const key = row.name?.trim();
          if (!key) return;

          if (!grouped[key]) {
            grouped[key] = {
              name: key,
              category: row.Category || "Uncategorized",
              MRP: row.MRP || "",
              batches: [],
            };
          }

          grouped[key].batches.push({
            batch: row.Batch || "",
            mfg: toMMYYYY(row.mfgDate),
            exp: toMMYYYY(row.expDate)
          });
        });

        const finalProducts = Object.values(grouped);
        setProducts(finalProducts);
        setFilteredProducts(finalProducts); // default
      } catch (err) {
        console.error("âŒ Fetch error:", err);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const result = products.filter((p) => {
      if (search.trim()) return p.name.toLowerCase().includes(search.toLowerCase());
      return p.category === selectedCategory;
    });
    setFilteredProducts(result);
  }, [products, search, selectedCategory]);

  return {
    products,
    filteredProducts,
    search,
    setSearch,
    selectedCategory,
    setSelectedCategory
  };
}

function toMMYYYY(isoDate) {
  if (!isoDate) return "";
  const d = new Date(isoDate);
  if (isNaN(d)) return "";
  return `${String(d.getMonth() + 1).padStart(2, "0")}-${d.getFullYear()}`;
}
