import exportToCSV from './exportToCSV';
import sendViaWhatsApp from './sendViaWhatsApp';

export default function submitOrder({ cart, dealer, vehicleName, vehicleNumber, notes, totalCTN }) {
  const dealerName = dealer?.["Dealer Name"] || "Unknown Dealer";

  // 1. Export CSV
  exportToCSV(cart, dealerName, vehicleName, vehicleNumber, notes);

  // 2. Build WhatsApp Message manually
  let msg = `üõí Jai Ayurved Factory Dispatch\n\n`;
  cart.forEach(item => {
    msg += `üîπ ${item.name}\n`;
    item.entries.forEach(entry => {
      msg += `   üì¶ Batch: ${entry.batch || '-'} | Qty: ${entry.qty || 0} | CTN: ${entry.ctn || 0}\n`;
    });
  });
  msg += `\nüöõ Vehicle: ${vehicleName || 'N/A'} (${vehicleNumber || 'N/A'})`;
  msg += `\nüì¶ Total CTNs: ${totalCTN || 0}`;
  if (notes) msg += `\nüìù Notes: ${notes}`;
  msg += `\n\nüì• *CSV file downloaded ‚Äî attach before sending*`;

  // 3. Send via WhatsApp
  sendViaWhatsApp("919929988408", msg); // Admin number
}
