// Updated Full App.js based on full analysis and structure

import React, { useState, useEffect } from "react";

// Components
import WelcomeScreen from "./components/WelcomeScreen";
import TopBar from "./components/TopBar";
import CategoryTabs from "./components/CategoryTabs";
import ProductGrid from "./components/ProductGrid";
import CartReview from "./components/CartReview";
import ReviewPanel from "./components/ReviewPanel";
import PreviewScreen from "./components/PreviewScreen";
import InvoiceCartReview from "./components/InvoiceCartReview";
import NewDealerForm from "./components/NewDealerForm";
import Navigation from "./components/Navigation";

// Hooks
import useToast from "./hooks/useToast";
import useCart from "./hooks/useCartLogic";
import useDealer from "./hooks/useDealerLogic";
import useProductFilter from "./hooks/useProductFilter";

// Utilities
import submitOrder from "./utils/submitOrder";

import "./styles/styles.css";

export default function App() {
  const [showWelcome, setShowWelcome] = useState(true);
  const [dealerCode, setDealerCode] = useState("");
  const [dealerPhone, setDealerPhone] = useState(null);
  const dealer = useDealer(dealerPhone);

  const [tab, setTab] = useState("categories");
  const [showRestorePrompt, setShowRestorePrompt] = useState(false);

  const { cart, handleAdd, handleRemove, handleBatchChange, handleAddBatch, handleSubmit, resetCart, totalCTN, vehicleName, setVehicleName, vehicleNumber, setVehicleNumber, notes, setNotes } = useCart(showToast);

  const { toast, showToast } = useToast();

  const { products, filteredProducts, search, setSearch, selectedCategory, setSelectedCategory } = useProductFilter();

  useEffect(() => {
    const timer = setTimeout(() => setShowWelcome(false), 3000);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart_v3") || "[]");
    if (storedCart.length > 0) setShowRestorePrompt(true);
  }, []);

  // Screens Switching
  if (showWelcome) {
    return <WelcomeScreen onFinish={() => setShowWelcome(false)} />;
  }

  if (!dealerPhone) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <h1 className="text-2xl font-bold mb-4">üîë Enter Dealer Login Code</h1>
        <input
          type="text"
          placeholder="Enter your code (e.g., JAI123)"
          value={dealerCode}
          onChange={(e) => setDealerCode(e.target.value)}
          className="border border-gray-300 rounded px-4 py-2 mb-4 w-full max-w-xs"
        />
        <button
          onClick={() => setDealerPhone(dealerCode)}
          className="bg-blue-600 text-white font-semibold px-6 py-2 rounded"
        >
          ‚úÖ Login
        </button>
      </div>
    );
  }

  if (!dealer && dealerPhone) {
    return <NewDealerForm phone={dealerPhone} onRegister={() => alert("‚úÖ Registered! Please refresh to continue.")} />;
  }

  const handleFinalOrderSubmit = async () => {
    const success = await submitOrder(cart, dealer, vehicleName, vehicleNumber, notes);
    if (success) {
      resetCart();
      setTab("categories");
      showToast("‚úÖ Order submitted successfully");
    } else {
      showToast("‚ö†Ô∏è Failed to submit order");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 px-4 py-6 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <TopBar dealerName={dealer?.["Dealer Name"]} />

        {showRestorePrompt && (
          <div className="bg-yellow-100 border border-yellow-300 text-yellow-900 p-4 rounded mb-6 text-center shadow">
            <p className="mb-2">üõí You have an unfinished order. Continue or start fresh?</p>
            <div className="flex justify-center gap-4">
              <button
                onClick={() => {
                  setShowRestorePrompt(false);
                  setTab("review");
                  showToast("‚úÖ Welcome back!");
                }}
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
              >‚úÖ Continue</button>
              <button
                onClick={() => {
                  resetCart();
                  setShowRestorePrompt(false);
                  setTab("categories");
                  showToast("üßπ Order cleared. Starting fresh.");
                }}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              >üßπ Start Fresh</button>
            </div>
          </div>
        )}

        <Navigation tab={tab} setTab={setTab} cartCount={cart.length} />

        {tab === "categories" && (
          <>
            <CategoryTabs currentCategory={selectedCategory} onSelect={setSelectedCategory} />
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-center gap-3 mb-4">
              <input
                type="text"
                placeholder="üîç Search..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="flex-1 px-4 py-3 border border-gray-300 rounded-md shadow-sm"
              />
            </div>
            <ProductGrid items={filteredProducts} cart={cart} onAdd={handleAdd} />
          </>
        )}

        {tab === "favorites" && (
          <>
            <h2 className="text-lg font-semibold mb-4">‚≠ê Favorites</h2>
            <ProductGrid items={filteredProducts} cart={cart} onAdd={handleAdd} />
          </>
        )}

        {tab === "review" && (
          <CartReview
            cart={cart}
            handleRemove={handleRemove}
            handleBatchChange={handleBatchChange}
            handleAddBatch={handleAddBatch}
            handleSubmit={() => setTab("finalReview")}
            totalCTN={totalCTN}
            vehicleName={vehicleName}
            setVehicleName={setVehicleName}
            vehicleNumber={vehicleNumber}
            setVehicleNumber={setVehicleNumber}
            notes={notes}
            setNotes={setNotes}
          />
        )}

        {tab === "finalReview" && (
          <ReviewPanel
            cart={cart}
            vehicleName={vehicleName}
            vehicleNumber={vehicleNumber}
            notes={notes}
            onPrint={() => setTab("preview")}
            onSendWhatsapp={handleFinalOrderSubmit}
          />
        )}

        {tab === "preview" && (
          <PreviewScreen
            cart={cart}
            vehicleName={vehicleName}
            vehicleNumber={vehicleNumber}
            notes={notes}
          />
        )}

        {tab === "invoice" && (
          <InvoiceCartReview
            cart={cart}
            vehicleName={vehicleName}
            vehicleNumber={vehicleNumber}
            notes={notes}
            onSubmit={handleFinalOrderSubmit}
          />
        )}

        {toast && <div className="fixed bottom-5 left-1/2 transform -translate-x-1/2 bg-black text-white px-4 py-2 rounded-xl text-sm">{toast}</div>}
      </div>
    </div>
  );
}
