// src/components/FactoryCartReview.js

import React from 'react';

export default function FactoryCartReview({
  cart,
  handleRemove,
  handleBatchChange,
  handleAddBatch,
  handleSubmit,
  totalCTN,
  vehicleName,
  setVehicleName,
  vehicleNumber,
  setVehicleNumber,
  notes,
  setNotes
}) {
  return (
    <div className="max-w-4xl mx-auto space-y-4 px-4 pb-32">
      {cart.length === 0 ? (
        <p className="text-center text-gray-600">üõí No items in cart.</p>
      ) : (
        cart.map((item, itemIndex) => (
          <div key={itemIndex} className="bg-white rounded-xl shadow-sm border border-blue-200 p-4">
            <div className="flex justify-between items-center mb-2">
              <h3 className="text-sm font-bold text-gray-800">{item.name}</h3>
              <button
                onClick={() => handleRemove(itemIndex)}
                className="text-red-500 hover:text-red-700 text-sm"
              >üóëÔ∏è Remove</button>
            </div>

            {item.entries.map((entry, batchIndex) => (
              <div key={batchIndex} className="flex flex-wrap gap-3 items-end mb-3 border p-2 rounded-md bg-gray-50">
                
                {/* Batch No */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">Batch</label>
                  <input
                    type="text"
                    value={entry.batch || ""}
                    onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'batch', e.target.value)}
                    className="border px-3 py-1 rounded w-28 text-sm"
                  />
                </div>

                {/* Qty */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">Qty (pcs)</label>
                  <input
                    type="number"
                    value={entry.qty || ""}
                    onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'qty', e.target.value)}
                    className="border px-3 py-1 rounded w-24 text-sm"
                  />
                </div>

                {/* CTN */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">CTN</label>
                  <input
                    type="number"
                    value={entry.ctn || ""}
                    onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'ctn', e.target.value)}
                    className="border px-3 py-1 rounded w-24 text-sm"
                  />
                </div>

              </div>
            ))}

            <button
              onClick={() => handleAddBatch(itemIndex)}
              className="text-blue-600 text-xs mt-2"
            >‚ûï Add Batch</button>

          </div>
        ))
      )}

      {cart.length > 0 && (
        <div className="bg-gray-100 border rounded-xl p-4 shadow-sm space-y-3 mt-6">
          <div className="text-right font-bold text-blue-600">
            Total CTN: {totalCTN}
          </div>

          <div className="text-center">
            <button
              onClick={handleSubmit}
              className="bg-green-600 hover:bg-green-700 text-white font-semibold py-3 px-6 rounded-lg mt-3"
            >
              ‚úÖ Submit Final Order
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
