import React from 'react';
function generateMonthOptions(yearsBack = 1, yearsForward = 5) {
  const now = new Date();
  const options = [];
  for (let y = now.getFullYear() - yearsBack; y <= now.getFullYear() + yearsForward; y++) {
    for (let m = 0; m < 12; m++) {
      const val = `${y}-${String(m + 1).padStart(2, "0")}`;
      const label = `${new Date(y, m).toLocaleString("default", {
        month: "short",
      })} ${y}`;
      options.push(<option key={val} value={val}>{label}</option>);
    }
  }
  return options;
}

export default function CartReview({
  cart,
  handleRemove,
  handleBatchChange,
  handleAddBatch,
  handleSubmit,
  totalCTN,
  vehicleName,
  setVehicleName,
  vehicleNumber,
  setVehicleNumber,
  notes,
  setNotes
}) {
  return (
    <div className="max-w-3xl mx-auto space-y-4 px-4 pb-32">
      {cart.length === 0 ? (
        <p className="text-center text-gray-600">üõí No items in cart.</p>
      ) : (
        cart.map((item, itemIndex) => (
          <div key={itemIndex} className="bg-white rounded-xl shadow-sm border border-blue-200 p-4">
            <div className="flex justify-between items-center mb-2">
              <h3 className="text-sm font-bold text-gray-800">{item.name}</h3>
              <button
                onClick={() => handleRemove(itemIndex)}
                className="text-red-500 hover:text-red-700 text-sm"
              >üóëÔ∏è Remove All</button>
            </div>

            {item.entries.map((entry, batchIndex) => (
              <div key={batchIndex} className="flex flex-wrap gap-3 items-end mb-3 border p-2 rounded-md bg-gray-50">
                {/* Batch No. */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">Batch No.</label>
                  <input
                    type="text"
                    value={entry.batch || ""}
                    onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'batch', e.target.value)}
                    className="border px-3 py-1 rounded w-32"
                  />
                </div>

                {/* MFG Date */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">MFG Date</label>
<select
  value={entry.mfg || ""}
  onChange={(e) => {
    console.log("‚úÖ MFG selected:", e.target.value); // Debug
    handleBatchChange(itemIndex, batchIndex, 'mfg', e.target.value);
  }}
  className="border px-3 py-1 rounded w-36 text-sm"
>
  <option value="">-- Select MFG --</option>
  {generateMonthOptions()}
</select>
                </div>

                {/* EXP Date */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">EXP Date</label>
             <select
  value={entry.exp || ""}
  onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'exp', e.target.value)}
  className="border px-3 py-1 rounded w-36 text-sm"
>
  <option value="">-- Select EXP --</option>
  {generateMonthOptions()}
</select>
   </div>

                {/* Qty Input */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">Qty (pcs)</label>
                  <input
                    type="number"
                    placeholder="Qty"
                    value={entry.qty || ""}
                    onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'qty', parseInt(e.target.value) || 0)}
                    min="0"
                    className={`border px-3 py-1 rounded w-24 ${entry.qty > 0 ? 'border-green-400' : 'border-gray-300'}`}
                  />
                </div>

                {/* Carton Input */}
                <div>
                  <label className="block text-xs text-gray-500 mb-1">CTN</label>
                  <input
                    type="number"
                    placeholder="CTN"
                    value={entry.ctn || ""}
                    onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'ctn', parseInt(e.target.value) || 0)}
                    min="0"
                    className={`border px-3 py-1 rounded w-24 ${entry.ctn > 0 ? 'border-green-400' : 'border-gray-300'}`}
                  />
                </div>

                {/* Display Total Quantity */}
                {(entry.qty > 0 && entry.ctn > 0) && (
                  <div className="text-sm font-semibold text-green-700">
                    Total Qty: {entry.qty * entry.ctn} pcs
                  </div>
                )}

                <button
                  onClick={() => handleRemove(itemIndex, batchIndex)}
                  className="text-xs text-red-500 ml-2"
                >‚ùå Remove</button>
              </div>
            ))}

            <button
              onClick={() => handleAddBatch(itemIndex)}
              className="text-blue-600 text-sm mt-2"
            >‚ûï Add Batch</button>
          </div>
        ))
      )}

      {/* Summary + Extra Fields */}
      {cart.length > 0 && (
        <div className="bg-gray-100 border rounded-xl p-4 shadow-sm space-y-3 mt-6">
          <div className="flex gap-4 flex-wrap">
            <div className="flex-1 min-w-[150px]">
              <label className="block text-sm font-medium text-gray-600">üöõ Vehicle Name</label>
              <input
                type="text"
                value={vehicleName}
                onChange={(e) => setVehicleName(e.target.value)}
                className="border px-3 py-2 rounded w-full"
              />
            </div>

            <div className="flex-1 min-w-[150px]">
              <label className="block text-sm font-medium text-gray-600">üî¢ Vehicle Number</label>
              <input
                type="text"
                value={vehicleNumber}
                onChange={(e) => setVehicleNumber(e.target.value)}
                className="border px-3 py-2 rounded w-full"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600">üìù Notes</label>
            <textarea
              rows={3}
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              className="border px-3 py-2 rounded w-full"
            />
          </div>

          <div className="text-right font-bold text-lg text-blue-700">Total CTN: {totalCTN}</div>

          <button
            onClick={handleSubmit}
            className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3 rounded-lg mt-3"
          >‚úÖ Submit Final Order</button>
        </div>
      )}
    </div>
  );
}
