// Updated ReviewPanel.js
import React from "react";
import exportTallyExcel from '../utils/exportTallyExcel';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import products from '../data/products';

export default function ReviewPanel({
  cart,
  vehicleName,
  vehicleNumber,
  notes,
  dealer,
  onPrint,
  onSendWhatsapp,
  handleBatchChange,
  setTab
}) {
  const totalCTN = cart.reduce((sum, item) =>
    sum + item.entries.reduce((eSum, e) => eSum + (e.ctn || 0), 0), 0);

  const handleExportTallyExcel = () => {
    exportTallyExcel(cart);
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();
    const date = new Date().toLocaleDateString('en-GB');

    doc.setFontSize(16);
    doc.text("🪔 JAI Ayurvedic Billing Dept", 14, 20);
    doc.setFontSize(10);
    doc.text(`Invoice Date: ${date}`, 14, 28);
    doc.text(`Dealer: ${dealer?.["Dealer Name"] || "Unknown"}`, 14, 34);
    doc.text(`Vehicle: ${vehicleName} (${vehicleNumber})`, 14, 40);
    if (notes) doc.text(`Notes: ${notes}`, 14, 46);

    const rows = cart.flatMap((item, idx) =>
      item.entries.map((entry, bIdx) => [
        idx + 1,
        item.name,
        entry.batch,
        entry.mfg,
        entry.exp,
        entry.qty,
        entry.ctn,
        (entry.qty || 0) * (entry.ctn || 0)
      ])
    );

    doc.autoTable({
      startY: 55,
      head: [["S.No", "Product Name", "Batch", "MFG", "EXP", "Qty/CTN", "CTN", "Total Qty"]],
      body: rows,
      styles: { fontSize: 8 }
    });

    const finalY = doc.lastAutoTable.finalY || 75;

    doc.text(`Total Cartons: ${totalCTN}`, 14, finalY + 10);
    doc.text(`Signature: ______________________`, 14, finalY + 20);

    doc.save(`Invoice_${Date.now()}.pdf`);
  };

  return (
    <div className="max-w-3xl mx-auto bg-white rounded-xl shadow-md p-6 space-y-4 print:text-sm">
      <h2 className="text-xl font-bold text-center border-b pb-2">📋 Order Review</h2>

      {cart.map((item, itemIndex) => (
        <div key={itemIndex} className="border p-3 rounded-md bg-gray-50">
          <h3 className="font-semibold text-gray-800">{item.name}</h3>
          <table className="w-full mt-2 text-sm">
            <thead>
              <tr className="text-left text-gray-500">
                <th>Batch</th>
                <th>MFG</th>
                <th>EXP</th>
                <th>Qty</th>
                <th>CTN</th>
              </tr>
            </thead>
            <tbody>
              {item.entries.map((entry, batchIndex) => (
                <tr key={batchIndex} className="border-t">
                  <td className="py-1">
                    <input
                      type="text"
                      value={entry.batch || (products.find(p => p.name === item.name)?.batches?.[0]?.batch || "")}
                      onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'batch', e.target.value)}
                      className="border rounded px-2 py-1 w-24"
                    />
                  </td>
                  <td className="py-1">
                    <input
                      type="text"
                      value={entry.mfg || (products.find(p => p.name === item.name)?.batches?.[0]?.mfg || "")}
                      onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'mfg', e.target.value)}
                      className="border rounded px-2 py-1 w-24"
                    />
                  </td>
                  <td className="py-1">
                    <input
                      type="text"
                      value={entry.exp || (products.find(p => p.name === item.name)?.batches?.[0]?.exp || "")}
                      onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'exp', e.target.value)}
                      className="border rounded px-2 py-1 w-24"
                    />
                  </td>
                  <td className="py-1">
                    <input
                      type="number"
                      value={entry.qty}
                      onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'qty', parseInt(e.target.value) || 0)}
                      className="border rounded px-2 py-1 w-20"
                    />
                  </td>
                  <td className="py-1">
                    <input
                      type="number"
                      value={entry.ctn}
                      onChange={(e) => handleBatchChange(itemIndex, batchIndex, 'ctn', parseInt(e.target.value) || 0)}
                      className="border rounded px-2 py-1 w-20"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}

      <div className="mt-6 space-y-1">
        <p><strong>🚛 Vehicle:</strong> {vehicleName}</p>
        <p><strong>🔢 Vehicle Number:</strong> {vehicleNumber}</p>
        <p><strong>📦 Total CTNs:</strong> {totalCTN}</p>
        {notes && <p><strong>📝 Notes:</strong> {notes}</p>}
      </div>

      <div className="flex flex-wrap justify-center gap-4 mt-6 print:hidden">
        <button
          onClick={() => setTab('categories')}
          className="bg-yellow-400 hover:bg-yellow-500 text-white font-semibold px-6 py-3 rounded-lg"
        >➕ Add More Items</button>

        <button
          onClick={() => window.print()}
          className="bg-gray-500 hover:bg-gray-700 text-white font-semibold px-6 py-3 rounded-lg"
        >🖨 Print Order</button>

        {dealer?.Type === "Billing" ? (
          <>
            <button
              onClick={handleExportTallyExcel}
              className="bg-blue-500 hover:bg-blue-600 text-white font-semibold px-6 py-3 rounded-lg"
            >📄 Export Excel (Tally)</button>

            <button
              onClick={handleGeneratePDF}
              className="bg-purple-500 hover:bg-purple-600 text-white font-semibold px-6 py-3 rounded-lg"
            >🖨 Generate Invoice PDF</button>
          </>
        ) : (
          <button
            onClick={onSendWhatsapp}
            className="bg-green-600 hover:bg-green-700 text-white font-semibold px-6 py-3 rounded-lg"
          >📲 Send to WhatsApp</button>
        )}
      </div>
    </div>
  );
}
